clear; close all; clc;% Test image contains values of the "peaks" function on a 1024 x 1024 grid% over the range [-3, 3]. The values are normalized to [0, 1].A = single(peaks(1024));A = A - min(A(:)) / (max(A(:) - min(A(:))));origScale = 1.55;pfmwrite(A, 'peaks.pfm', origScale);[B, scale] = pfmread('peaks.pfm');assert(all(size(A) == size(B)));imshow([A B])atol = 1e-6;assert(all(A(:) == B(:)));assert(abs(origScale - scale) <= atol);